import React, { useState, useRef, useEffect } from "react";
import "./App.css";

function App() {
  const [input, setInput] = useState("");
  const [messages, setMessages] = useState([]); // {sender: "user" | "bot", text: string}
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    const question = input.trim();
    if (!question) return;

    // Add user message
    setMessages((prev) => [...prev, { sender: "user", text: question }]);
    setInput("");
    setLoading(true);

    try {
      const res = await fetch("http://127.0.0.1:5000/ask", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ question }),
      });
      const data = await res.json();
      if (data.error) {
        setMessages((prev) => [...prev, { sender: "bot", text: "Error: " + data.error }]);
      } else {
        setMessages((prev) => [...prev, { sender: "bot", text: data.answer }]);
      }
    } catch {
      setMessages((prev) => [...prev, { sender: "bot", text: "Failed to connect to the backend." }]);
    }

    setLoading(false);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="App" style={{ maxWidth: 600, margin: "2rem auto", fontFamily: "Arial, sans-serif" }}>
      <h1 style={{ textAlign: "center" }}>Trading AI Chatbot</h1>

      <div className="chat-window">
        {messages.length === 0 && !loading && (
          <div className="chat-start">Ask a question about your trading data...</div>
        )}

        {messages.map((msg, idx) => (
          <div key={idx} className={`chat-message ${msg.sender === "user" ? "user" : "bot"}`}>
            <div className="chat-bubble">{msg.text}</div>
          </div>
        ))}

        {loading && (
          <div className="chat-message bot">
            <div className="chat-bubble">Thinking...</div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      <textarea
        rows={2}
        placeholder="Type your question and press Enter..."
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={handleKeyDown}
        disabled={loading}
        className="chat-input"
      />
      <button onClick={sendMessage} disabled={loading || !input.trim()} className="chat-send-button">
        Send
      </button>
    </div>
  );
}

export default App;
